#define HOT_TUB_BUTTON_INPUT_PIN     A7   // Note: A6 & A7 on pro-mini can't be used as digital input, only analog
#define JETS_BUTTON_INPUT_PIN        A2
#define BUBBLER_BUTTON_INPUT_PIN     A6
#define ENCODERPB                    12    // Encoder pushbutton - rotates LCD text 180 degrees
#define MAX_I2C_BYTES 6  // Max bytes master will send

// I2C Commands
enum {
  CMD_SAVE_ALL    =  1,  // writes all data from Mater to slave
  CMD_SLAVE_ID    =  2,
  CMD_ONOFF_BTN   =  3,
  CMD_PUMP_BTN    =  4,
  CMD_BUBBLE_BTN  =  5,
  CMD_TEMP_SETPT  =  6,
  ADR_ONOFF_STAT  =  7, // Addresses to save data to slave
  ADR_PUMP_STAT   =  8,
  ADR_BUBBLE_STAT =  9,
  ADR_HEATER_STAT = 10,
  ADR_TEMP_SETPT  = 11
};
const byte SLAVE_ID = 46;


#define BTN_ON LOW // Pushbuttons are low if they are on
const uint8_t debounceDelay = 200; // debounce time in mS

class HotTub
{
  // need to define the variables as members of the 'class' because they are used in a static class
  // Reference: http://www.cs.loyola.edu/~lawrie/CS301/F03/StaticClassMembers.htm 
  static bool _isHotTubOn;
  static bool _isBubblerOn;
  static bool _isPumpOn;
  static bool _isHeaterOn;
  static int  _tempSetpoint;
  static int  _tempActual;
  static byte _i2cCmd;
  static uint32_t _buttonChangeTime;

public:
  // Public restriction allows any part of the program, including parts outside the class,
  // to access the functions and variables specified as public.
  HotTub();  // Constructor - called when the class is first called
  ~HotTub();  // Destructor
  void begin(); // setup pinMode
  bool isHotTubOn();
  bool isPumpOn();
  bool isBubblerOn();
  bool isHeaterOn();
  bool isDisplayInverted();
  void setHotTubOn();
  void setHotTubOff();
  void setPumpOn();
  void setPumpOff();
  uint32_t getPumpOnTime();  // returns millis time when pump was turned on
  void setBubblerOn();
  void setBubblerOff();
  uint32_t getBubblerOnTime();  // returns millis time when bubbler was turned on
  bool processButtons(); // read inputs for pushbuttons including encoder pushbutton
  int  getWaterTemp();
  int  getWaterTempDefault();
  void setWaterTemp(int tempSetpoint);
  int  getWaterTempSetpoint();
  bool isI2cTimeout(int threshold);  // compares last time there was I2C communication to threshold

protected:

private:
  uint32_t _debounceTimout;  // used to debounce pushbuttons
  bool _isDisplayInverted;
  uint32_t _bubblerStartTime;  // used to turn bubbles off after 15 minutes
  uint32_t _pumpStartTime;     // used to turn pump off after 30 minutes

  static void i2cReceiveCmd(int bytesReceived);  // I2C Receive Event.  Make static so you don't need an instance of the class
  static void i2cSendData(); // sends data back to master

};




